var linkBasketAll=document.querySelectorAll(".product-buy"),basket=document.querySelector(".modal-basket"),close=basket.querySelector(".close-btn"),basketContinue=basket.querySelector(".btn-continue");for(var linkBasket of linkBasketAll)linkBasket.addEventListener("click",function(e){e.preventDefault(),basket.classList.add("modal-show")});basketContinue.addEventListener("click",function(e){e.preventDefault(),basket.classList.remove("modal-show")}),close.addEventListener("click",function(e){e.preventDefault(),basket.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&basket.classList.contains("modal-show")&&(e.preventDefault(),basket.classList.remove("modal-show"))});